#import "main.h"#import <stdio.h>@implementation HelloWorld-(void) print {	printf( "Hello NSObject World\n");}@endint main(int argc, char *argv[]){	NSAutoreleasePool *pool = [[NSAutoreleasePool alloc] init];     	// create a new instance	HelloWorld *pHelloWorld = [[[HelloWorld alloc] init] autorelease];		[pHelloWorld print];	//[pHelloWorld release];			char buf[100] = "Hello World 한글";	NSString* helloString = [[NSString stringWithCString:buf encoding:NSUTF8StringEncoding] autorelease];			NSLog(@"%@",helloString);		[helloString release];		if( pool != nil ) {		[pool drain]; 		[NSThread sleepForTimeInterval:5];			}		return 0;}/* EOF */
